  observeEvent( input$date , {
    updateSelectInput(
      session, 
      "year", 
      selected = year(input$date)
    )
  })

  # We want the date to be update whenever 
  # the selectInput is updated
  observeEvent( input$year , {
    updateDateInput(
      session, 
      "date", 
      value = lubridate::as_date(
        sprintf("%s-01-01", input$year)
      )
    )
  })

}

