for (i in 1:5){
  future({
    # Faking a random computation time
    Sys.sleep( sample(1:5, 1) )
    return(
      list(
        id = i,
        res = rnorm(5)
      )
    )
  }) %...>% 
    # Whenever we receive an output, we add it to 
    # the queue database
    (function(results){
      publish(
        queue, 
        title = as.character(results$i), 
        message = paste(
          results$res, 
          collapse = ","
        )
      )
    }) %...!% 
    # If ever we have an error, we return it as a warning
    warning(.)
